import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { 
  TrendingUp, 
  TrendingDown, 
  Plus, 
  Trash2, 
  BarChart3,
  RefreshCw,
  DollarSign,
  Activity,
  AlertCircle,
  X,
  LineChart,
  Search,
  Star,
  ArrowUp,
  Trophy
} from 'lucide-react';

const API_BASE = 'http://127.0.0.1:5000/api';

// Utility functions
const formatCurrency = (amount) => {
  if (!amount && amount !== 0) return 'N/A';
  return new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
    minimumFractionDigits: 2,
    maximumFractionDigits: 6
  }).format(amount);
};

const formatPercentage = (value) => {
  const num = Number(value);
  if (isNaN(num)) return 'N/A';
  return `${num >= 0 ? '+' : ''}${num.toFixed(2)}%`;
};

const formatDate = (dateString) => {
  return new Date(dateString).toLocaleDateString('en-US', {
    month: 'short',
    day: 'numeric',
    year: 'numeric'
  });
};

// Custom hooks
const useApi = () => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const apiCall = useCallback(async (endpoint, options = {}) => {
    try {
      setLoading(true);
      setError('');
      
      const response = await fetch(`${API_BASE}${endpoint}`, {
        headers: {
          'Content-Type': 'application/json',
          ...options.headers
        },
        ...options
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      return await response.json();
    } catch (err) {
      const errorMessage = `API Error: ${err.message}`;
      setError(errorMessage);
      throw new Error(errorMessage);
    } finally {
      setLoading(false);
    }
  }, []);

  return { apiCall, loading, error, setError };
};

// Components
const LoadingSpinner = ({ message = "Loading..." }) => (
  <div className="flex items-center justify-center py-12">
    <RefreshCw className="animate-spin text-blue-600 mr-2" size={24} />
    <span className="text-gray-600">{message}</span>
  </div>
);

const ErrorAlert = ({ error, onClose }) => (
  error && (
    <div className="mb-6 bg-red-50 border border-red-200 rounded-lg p-4 flex items-center">
      <AlertCircle className="h-5 w-5 text-red-400 mr-2" />
      <span className="text-red-700">{error}</span>
      <button
        onClick={onClose}
        className="ml-auto text-red-400 hover:text-red-600"
      >
        <X size={16} />
      </button>
    </div>
  )
);

const CoinTableRow = React.memo(({ coin, index, page, perPage, onAddToPortfolio, showYearlyGrowth = false }) => (
  <tr className="hover:bg-gray-50">
    <td className="px-6 py-4 text-sm text-gray-900">
      #{((page - 1) * perPage) + index + 1}
    </td>
    <td className="px-6 py-4">
      <div>
        <div className="font-medium text-gray-900">{coin.name}</div>
        <div className="text-sm text-gray-500">{coin.symbol}</div>
      </div>
    </td>
    <td className="px-6 py-4 text-sm text-gray-900">
      {formatCurrency(coin.current_price)}
    </td>
    <td className="px-6 py-4 text-sm">
      <span className={`flex items-center ${
        coin.price_change_percentage_24h >= 0 ? 'text-green-600' : 'text-red-600'
      }`}>
        {coin.price_change_percentage_24h >= 0 ? <TrendingUp size={16} /> : <TrendingDown size={16} />}
        {formatPercentage(coin.price_change_percentage_24h)}
      </span>
    </td>
    {showYearlyGrowth && (
      <td className="px-6 py-4 text-sm">
        <span className={`flex items-center font-semibold ${
          coin.price_change_percentage_1y >= 0 ? 'text-green-600' : 'text-red-600'
        }`}>
          <Trophy size={16} className="mr-1" />
          {formatPercentage(coin.price_change_percentage_1y)}
        </span>
      </td>
    )}
    <td className="px-6 py-4 text-sm text-gray-900">
      {formatCurrency(coin.market_cap)}
    </td>
    <td className="px-6 py-4 text-sm">
      <button
        onClick={() => onAddToPortfolio(coin)}
        className="text-blue-600 hover:text-blue-900 mr-2"
        title="Add to Portfolio"
      >
        <Plus size={16} />
      </button>
    </td>
  </tr>
));

const PortfolioRow = React.memo(({ item, onRemove }) => (
  <tr className="hover:bg-gray-50">
    <td className="px-6 py-4">
      <div>
        <div className="font-medium text-gray-900">{item.coin_name}</div>
        <div className="text-sm text-gray-500">{item.symbol}</div>
      </div>
    </td>
    <td className="px-6 py-4 text-sm text-gray-900">
      {item.quantity}
    </td>
    <td className="px-6 py-4 text-sm text-gray-900">
      {formatCurrency(item.current_price)}
    </td>
    <td className="px-6 py-4 text-sm text-gray-900">
      {formatCurrency(item.quantity * item.current_price)}
    </td>
    <td className="px-6 py-4 text-sm">
      <span className={`flex items-center ${
        item.change_24h >= 0 ? 'text-green-600' : 'text-red-600'
      }`}>
        {item.change_24h >= 0 ? <TrendingUp size={16} /> : <TrendingDown size={16} />}
        {formatPercentage(item.change_24h)}
      </span>
    </td>
    <td className="px-6 py-4 text-sm">
      <button
        onClick={() => onRemove(item.id)}
        className="text-red-600 hover:text-red-900"
        title="Remove from Portfolio"
      >
        <Trash2 size={16} />
      </button>
    </td>
  </tr>
));

const WatchlistRow = React.memo(({ item, onAddToPortfolio, onRemove }) => (
  <tr className="hover:bg-gray-50">
    <td className="px-6 py-4">
      <div>
        <div className="font-medium text-gray-900">{item.coin_name}</div>
        <div className="text-sm text-gray-500">{item.symbol}</div>
      </div>
    </td>
    <td className="px-6 py-4 text-sm text-gray-900">
      {formatCurrency(item.current_price)}
    </td>
    <td className="px-6 py-4 text-sm">
      <span className={`flex items-center ${
        item.change_24h >= 0 ? 'text-green-600' : 'text-red-600'
      }`}>
        {item.change_24h >= 0 ? <TrendingUp size={16} /> : <TrendingDown size={16} />}
        {formatPercentage(item.change_24h)}
      </span>
    </td><td className="px-6 py-4 text-sm">
      <button
        onClick={() => onAddToPortfolio(item)}
        className="text-blue-600 hover:text-blue-900 mr-2"
        title="Add to Portfolio"
      >
        <Plus size={16} />
      </button>
      <button
        onClick={() => onRemove(item.id)}
        className="text-red-600 hover:text-red-900"
        title="Remove from Watchlist"
      >
        <Trash2 size={16} />
      </button>
    </td>
  </tr>
));

const AddToPortfolioModal = ({ isOpen, onClose, coin, onAdd }) => {
  const [quantity, setQuantity] = useState('');

  if (!isOpen || !coin) return null;

  const handleSubmit = (e) => {
    e.preventDefault();
    if (quantity && parseFloat(quantity) > 0) {
      onAdd(coin, parseFloat(quantity));
      setQuantity('');
      onClose();
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 w-96">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-lg font-semibold">Add to Portfolio</h3>
          <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
            <X size={20} />
          </button>
        </div>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <p className="text-sm text-gray-600 mb-2">
              Adding {coin.name} ({coin.symbol?.toUpperCase()})
            </p>
            <p className="text-sm text-gray-500 mb-3">
              Current Price: {formatCurrency(coin.current_price)}
            </p>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Quantity
            </label>
            <input
              type="number"
              step="any"
              min="0"
              value={quantity}
              onChange={(e) => setQuantity(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Enter quantity"
              required
            />
          </div>
          <div className="flex justify-end space-x-3">
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50"
            >
              Cancel
            </button>
            <button
              type="submit"
              className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
            >
              Add to Portfolio
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

// Main component
const CryptoDashboard = () => {
  const [activeTab, setActiveTab] = useState('market');
  const [coins, setCoins] = useState([]);
  const [topGrowthCoins, setTopGrowthCoins] = useState([]);
  const [portfolio, setPortfolio] = useState([]);
  const [watchlist, setWatchlist] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState('');
  const [showAddModal, setShowAddModal] = useState(false);
  const [selectedCoin, setSelectedCoin] = useState(null);
  const [showTopGrowthCoins, setShowTopGrowthCoins] = useState(false);
  const [portfolioStats, setPortfolioStats] = useState({});
  
  const { apiCall, loading, error, setError } = useApi();
  const coinsPerPage = 20;

  // Filter coins based on search
  const filteredCoins = useMemo(() => {
    if (!searchTerm.trim()) return coins;
    return coins.filter(coin => 
      coin.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      coin.symbol.toLowerCase().includes(searchTerm.toLowerCase())
    );
  }, [coins, searchTerm]);

  const paginatedCoins = useMemo(() => {
    const startIndex = (currentPage - 1) * coinsPerPage;
    return filteredCoins.slice(startIndex, startIndex + coinsPerPage);
  }, [filteredCoins, currentPage]);

  const totalPages = Math.ceil(filteredCoins.length / coinsPerPage);

  // API calls
  const fetchCoins = useCallback(async () => {
    try {
      const data = await apiCall('/coins');
      setCoins(data);
    } catch (err) {
      console.error('Failed to fetch coins:', err);
    }
  }, [apiCall]);

  const fetchTopGrowthCoins = useCallback(async () => {
    try {
      const data = await apiCall('/coins/top-growth');
      setTopGrowthCoins(data);
    } catch (err) {
      console.error('Failed to fetch top growth coins:', err);
    }
  }, [apiCall]);

  const fetchPortfolio = useCallback(async () => {
    try {
      const data = await apiCall('/portfolio');
      setPortfolio(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error('Failed to fetch portfolio:', err);
      setPortfolio([]); // fallback to empty array on error
    }
  }, [apiCall]);

  const fetchWatchlist = useCallback(async () => {
    try {
      const response = await fetch('/api/watchlist');
      const data = await response.json();
      setWatchlist(Array.isArray(data.watchlist) ? data.watchlist : []);
    } catch (err) {
      console.error('Failed to fetch watchlist:', err);
    }
  }, [apiCall]);

  const fetchPortfolioStats = useCallback(async () => {
    try {
      const data = await apiCall('/portfolio/stats');
      setPortfolioStats(data);
    } catch (err) {
      console.error('Failed to fetch portfolio stats:', err);
    }
  }, [apiCall]);

  // CRUD operations
  const addToPortfolio = async (coin, quantity) => {
    try {
      await apiCall('/portfolio', {
        method: 'POST',
        body: JSON.stringify({
          coin_id: coin.id,
          coin_name: coin.name,
          symbol: coin.symbol,
          quantity: quantity
        })
      });
      fetchPortfolio();
      fetchPortfolioStats();
    } catch (err) {
      console.error('Failed to add to portfolio:', err);
    }
  };

  const removeFromPortfolio = async (id) => {
    try {
      await apiCall(`/portfolio/${id}`, { method: 'DELETE' });
      fetchPortfolio();
      fetchPortfolioStats();
    } catch (err) {
      console.error('Failed to remove from portfolio:', err);
    }
  };

  const addToWatchlist = async (coin) => {
    try {
      await apiCall('/watchlist', {
        method: 'POST',
        body: JSON.stringify({
          coin_id: coin.id,
          coin_name: coin.name,
          symbol: coin.symbol
        })
      });
      fetchWatchlist();
    } catch (err) {
      console.error('Failed to add to watchlist:', err);
    }
  };

  const removeFromWatchlist = async (id) => {
    try {
      await apiCall(`/watchlist/${id}`, { method: 'DELETE' });
      fetchWatchlist();
    } catch (err) {
      console.error('Failed to remove from watchlist:', err);
    }
  };

  // Effects
  useEffect(() => {
    fetchCoins();
    fetchPortfolio();
    fetchWatchlist();
    fetchPortfolioStats();
  }, [fetchCoins, fetchPortfolio, fetchWatchlist, fetchPortfolioStats]);

  // Event handlers
  const handleAddToPortfolio = (coin) => {
    setSelectedCoin(coin);
    setShowAddModal(true);
  };

  const handleShowTopGrowth = () => {
    if (!showTopGrowthCoins) {
      fetchTopGrowthCoins();
    }
    setShowTopGrowthCoins(!showTopGrowthCoins);
  };

  const refreshData = () => {
    fetchCoins();
    fetchPortfolio();
    fetchWatchlist();
    fetchPortfolioStats();
    if (showTopGrowthCoins) {
      fetchTopGrowthCoins();
    }
  };

  // Render functions
  const renderMarketTab = () => (
    <div className="space-y-6">
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
        <div className="flex items-center space-x-4">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
            <input
              type="text"
              placeholder="Search coins..."
              value={searchTerm}
              onChange={(e) => {
                setSearchTerm(e.target.value);
                setCurrentPage(1);
              }}
              className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <button
            onClick={handleShowTopGrowth}
            className={`flex items-center px-4 py-2 rounded-lg font-medium transition-colors ${
              showTopGrowthCoins 
                ? 'bg-green-600 text-white hover:bg-green-700' 
                : 'bg-green-100 text-green-700 hover:bg-green-200'
            }`}
          >
            <Trophy className="mr-2" size={16} />
            {showTopGrowthCoins ? 'Show All Coins' : 'Top Growth (1Y)'}
          </button>
        </div>
        <button
          onClick={refreshData}
          disabled={loading}
          className="flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50"
        >
          <RefreshCw className={`mr-2 ${loading ? 'animate-spin' : ''}`} size={16} />
          Refresh
        </button>
      </div>

      <div className="bg-white rounded-lg shadow overflow-hidden">
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Rank
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Name
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Price
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  24h Change
                </th>
                {showTopGrowthCoins && (
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    <div className="flex items-center">
                      <Trophy className="mr-1" size={14} />
                      1Y Growth
                    </div>
                  </th>
                )}
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Market Cap
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {(showTopGrowthCoins ? topGrowthCoins : paginatedCoins).map((coin, index) => (
                <CoinTableRow
                  key={coin.id}
                  coin={coin}
                  index={index}
                  page={currentPage}
                  perPage={coinsPerPage}
                  onAddToPortfolio={handleAddToPortfolio}
                  showYearlyGrowth={showTopGrowthCoins}
                />
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {!showTopGrowthCoins && totalPages > 1 && (
        <div className="flex justify-center items-center space-x-2">
          <button
            onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
            disabled={currentPage === 1}
            className="px-3 py-2 border border-gray-300 rounded-md disabled:opacity-50 hover:bg-gray-50"
          >
            Previous
          </button>
          <span className="px-4 py-2 text-sm text-gray-600">
            Page {currentPage} of {totalPages}
          </span>
          <button
            onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
            disabled={currentPage === totalPages}
            className="px-3 py-2 border border-gray-300 rounded-md disabled:opacity-50 hover:bg-gray-50"
          >
            Next
          </button>
        </div>
      )}
    </div>
  );

  const renderPortfolioTab = () => (
    <div className="space-y-6">
      {portfolioStats && Object.keys(portfolioStats).length > 0 && (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="bg-white p-6 rounded-lg shadow">
            <div className="flex items-center">
              <DollarSign className="h-8 w-8 text-green-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-500">Total Value</p>
                <p className="text-2xl font-bold text-gray-900">
                  {formatCurrency(portfolioStats.total_value)}
                </p>
              </div>
            </div>
          </div>
          <div className="bg-white p-6 rounded-lg shadow">
            <div className="flex items-center">
              <Activity className="h-8 w-8 text-blue-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-500">24h Change</p>
                <p className={`text-2xl font-bold ${
                  portfolioStats.total_change_24h >= 0 ? 'text-green-600' : 'text-red-600'
                }`}>
                  {formatPercentage(portfolioStats.total_change_24h)}
                </p>
              </div>
            </div>
          </div>
          <div className="bg-white p-6 rounded-lg shadow">
            <div className="flex items-center">
              <BarChart3 className="h-8 w-8 text-purple-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-500">Holdings</p>
                <p className="text-2xl font-bold text-gray-900">
                  {portfolio.length}
                </p>
              </div>
            </div>
          </div>
        </div>
      )}

      <div className="bg-white rounded-lg shadow overflow-hidden">
        <div className="px-6 py-4 border-b border-gray-200">
          <h3 className="text-lg font-medium text-gray-900">Portfolio Holdings</h3>
        </div>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Coin
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Quantity
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Price
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Value
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  24h Change
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {portfolio.map((item) => (
                <PortfolioRow
                  key={item.id}
                  item={item}
                  onRemove={removeFromPortfolio}
                />
              ))}
              {portfolio.length === 0 && (
                <tr>
                  <td colSpan="6" className="px-6 py-8 text-center text-gray-500">
                    No holdings in your portfolio. Add some coins from the Market tab!
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  const renderWatchlistTab = () => (
    <div className="space-y-6">
      <div className="bg-white rounded-lg shadow overflow-hidden">
        <div className="px-6 py-4 border-b border-gray-200">
          <h3 className="text-lg font-medium text-gray-900">Watchlist</h3>
        </div>
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Coin
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Price
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  24h Change
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {watchlist.map((item) => (
                <WatchlistRow
                  key={item.id}
                  item={item}
                  onAddToPortfolio={handleAddToPortfolio}
                  onRemove={removeFromWatchlist}
                />
              ))}
              {watchlist.length === 0 && (
                <tr>
                  <td colSpan="4" className="px-6 py-8 text-center text-gray-500">
                    No coins in your watchlist. Add some coins from the Market tab!
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-100">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900">Crypto Dashboard</h1>
          <p className="mt-2 text-gray-600">Track your cryptocurrency investments</p>
        </div>

        <ErrorAlert error={error} onClose={() => setError('')} />

        <div className="mb-6">
          <nav className="flex space-x-8">
            {[
              { id: 'market', label: 'Market', icon: LineChart },
              { id: 'portfolio', label: 'Portfolio', icon: BarChart3 },
              { id: 'watchlist', label: 'Watchlist', icon: Star }
            ].map(({ id, label, icon: Icon }) => (
              <button
                key={id}
                onClick={() => setActiveTab(id)}
                className={`flex items-center px-1 py-4 border-b-2 font-medium text-sm ${
                  activeTab === id
                    ? 'border-blue-500 text-blue-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700'
                }`}
              >
                <Icon className="mr-2" size={18} />
                {label}
              </button>
            ))}
          </nav>
        </div>

        {loading && <LoadingSpinner />}

        {!loading && (
          <>
            {activeTab === 'market' && renderMarketTab()}
            {activeTab === 'portfolio' && renderPortfolioTab()}
            {activeTab === 'watchlist' && renderWatchlistTab()}
          </>
        )}

        <AddToPortfolioModal
          isOpen={showAddModal}
          onClose={() => setShowAddModal(false)}
          coin={selectedCoin}
          onAdd={addToPortfolio}
        />
      </div>
    </div>
  );
};

export default CryptoDashboard;
